public class AppBootstrapper : FrameworkBootstrapper<MainFrameViewModel>
                {
                protected override void PrepareCompositionContainer(CompositionBatch batch)
                {
                base.PrepareCompositionContainer(batch);
             
                // Inject the authentication service into the framework.
                batch.AddExportedValue<IAuthenticationService>(new AuthenticationService());
                }
                }

public class AppBootstrapper : FrameworkBootstrapper<MainFrameViewModel>
{
    protected override void PrepareCompositionContainer(CompositionBatch batch)
    {
        base.PrepareCompositionContainer(batch);
 
        // Inject the authentication service into the framework.
        batch.AddExportedValue<IAuthenticationService>(new AuthenticationService<NorthwindIBEntities>());
    }
}

public class LoginViewModel
{
    public LoginViewModel()
    {
        Busy = new BusyWatcher();
    }
 
    public IBusyWatcher Busy { get; private set; }
 
    public async void Login()
    {
        using (Busy.GetTicket())
        {
            // Snip .. removed for clarity
            await _authenticationService.LoginAsync(credential);
 
            if (_authenticationService.IsLoggedIn)
                TryClose();
        }
    }
}

member.example:
This example shows how to configure and export and EntityManagerProvider using the fake store.
            
                public class EntityManagerProviderFactory
                {
                    [Export]
                    public IEntityManagerProvider<TempHireEntities> TempHireEntityManagerProvider
                    {
                        get
                        {
                            return new EntityManagerProvider<TempHireEntities>().With(ConnectionOptions.Fake.Name);
                        }
                    }
                }

